var com, pin, serialPort, bit;

pin = 11;

com = require("serialport");

serialPort = new com.SerialPort("/dev/cu.usbmodem621", {
  baudrate: 57600,
  buffersize: 1
});

serialPort.on("open", function() {
  serialPort.write([0xF4, pin, 0x01]);
  return setInterval((function() {
    var p, portValue;
    bit ^= 0x01;
    p = pin / 8 | 0;
    portValue = bit * 32;
    return serialPort.write([0x90 | p, portValue & 0x7F, 0]);
  }), 1000);
});
